import React from "react";
import { Route } from "react-router-dom";
import axios from "axios";
import Contact from "./components/Contact";
import SignIn from "./components/SignIn";
import SearchResults from "./components/SearchResults/SearchResults";
import NavBar from "./components/NavBar";
import NewUser from "./components/NewUser";
import Message from "./components/Chat/Message";
import MessageForm from "./components/Chat/MessageForm";
import MessageList from "./components/Chat/MessageList";

// const API_KEY = "duywYDviRp03Bk2OT6ZrkE0Ccl3ODlUA";

// Background video

// <video autoplay muted loop id="myVideo">
//   <source src="looping_clouds.mp4" type="video/mp4"></source>
// </video>
//



class App extends React.Component {
  constructor() {
    super();
    this.state = {
      loggedIn: false,
      email: null
    };
  }

  componentDidMount = () => {
  };

  updateUser = userObject => {
    this.setState(userObject);
  };

  getUser = () => {
    axios.get("/user").then(response => {
      console.log("Get User response: ");
      console.log(response.data);
      if (response.data.user) {
        console.log("Get User: There is a user saved in server: ");
        this.setState({
          loggedIn: true,
          email: response.data.user.email
        });
      } else {
        console.log("Get user: no user");
        this.setState({
          loggedIn: false,
          email: null
        });
      }
    });
  };
  // state = {
  //   messages: []
  // };


  // handleNewMessage = (text) => {
  //   this.setState({
  //     messages: [...this.state.messages, { me: true, author: "Me", body: text }],
  //   });
  // }
  render() {
    return (
      <div className="App">
        <NavBar updateUser={this.updateUser} loggedIn={this.state.loggedIn} />
        <MessageList messages={this.state.messages} />
        <MessageForm onMessageSend={this.handleNewMessage} />
        <Message />
        <Route exact path="/" component={NewUser} />
        <Route
          path="/signin"
          render={() => <SignIn updateUser={this.updateUser} />}
        />
        <Route path="/newuser" component={NewUser} />
        <Route path="/main" component={SearchResults} />
        <Route path="/contact" component={Contact} />
        <video autoPlay id="background-video" loop autoplay>
          <source control autoPlay src="https://www.youtube.com/watch?v=GOFtYeo64JE&feature=youtu.be" type="link"></source>
          <source control autoPlay src="http://video2.ignitemotion.com/files/mp4/Looping_Clouds.mp4" type="video/mp4"></source>
          <source control autoPlay src="flying_through_clouds.mov" type="mov" ></source>

          Your browser does not support the video tag.
        </video>
      </div>
    );
  }
}
export default App;
